import React, { useState } from 'react';
import axios from 'axios';

const UserJobSearch = () => {
  const [form, setForm] = useState({ skills: '', experience: '', location: '' });
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setJobs([]);
    try {
      // Replace with your actual API key and endpoint
      const apiKey = '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';
      const response = await axios.get('https://jsearch.p.rapidapi.com/search', {
        params: {
          query: `${form.skills} ${form.experience}`,
          location: form.location,
          page: 1,
          num_pages: 1
        },
        headers: {
          'X-RapidAPI-Key': apiKey,
          'X-RapidAPI-Host': 'jsearch.p.rapidapi.com'
        }
      });
      setJobs(response.data.data || []);
    } catch (err) {
      setError('Failed to fetch jobs. Please try again.');
    }
    setLoading(false);
  };

  return (
    <div style={{ maxWidth: 500, margin: "2rem auto" }}>
      <h2>Find Jobs Matching Your Profile</h2>
      <form onSubmit={handleSubmit}>
        <input
          name="skills"
          placeholder="Skills (e.g. JavaScript, Python)"
          value={form.skills}
          onChange={handleChange}
          required
        /><br /><br />
        <input
          name="experience"
          placeholder="Experience (e.g. 3 years, Senior)"
          value={form.experience}
          onChange={handleChange}
          required
        /><br /><br />
        <input
          name="location"
          placeholder="Location (e.g. London, Remote)"
          value={form.location}
          onChange={handleChange}
          required
        /><br /><br />
        <button type="submit" disabled={loading}>
          {loading ? 'Searching...' : 'Search Jobs'}
        </button>
      </form>
      {error && <div style={{ color: 'red', marginTop: '1rem' }}>{error}</div>}
      <div style={{ marginTop: "2rem" }}>
        {jobs.length > 0 && <h3>Job Results:</h3>}
        <ul>
          {jobs.map((job, idx) => (
            <li key={idx} style={{ marginBottom: '1.5rem' }}>
              <strong>{job.job_title}</strong> at {job.employer_name}<br />
              <span>{job.job_city}, {job.job_country}</span><br />
              <a href={job.job_apply_link} target="_blank" rel="noopener noreferrer">View Job</a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default UserJobSearch;
