import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './Login';
import Register from './Register';
import Homepage from './Homepage'; // Import your Homepage component

function App() {
  const [user, setUser] = useState(null);
  const isAuthenticated = !!localStorage.getItem('token');

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={isAuthenticated ? <Navigate to="/homepage" /> : <Navigate to="/login" />}
        />
        <Route 
          path="/login" 
          element={isAuthenticated ? <Navigate to="/homepage" /> : <Login setUser={setUser} />} 
        />
        <Route 
          path="/register" 
          element={isAuthenticated ? <Navigate to="/homepage" /> : <Register setUser={setUser} />} 
        />
        <Route
          path="/homepage"
          element={
            isAuthenticated ? (
              <Homepage user={user} /> // Your actual homepage component
            ) : (
              <Navigate to="/login" />
            )
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
