import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate, useNavigate } from 'react-router-dom';
import Login from './Login';
import Register from './Register';

// Centered HomePage with Register button
const HomePage = () => {
  const navigate = useNavigate();

  return (
    <div style={styles.container}>
      <h1>Welcome to the Login App</h1>
      <div style={styles.buttonGroup}>
        <button style={styles.button} onClick={() => navigate('/login')}>Login</button>
        <button style={styles.button} onClick={() => navigate('/register')}>Register</button>
      </div>
    </div>
  );
};

// Dummy Dashboard
const Dashboard = ({ user }) => (
  <div style={styles.container}>
    <h2>Welcome, {user?.username || 'User'}!</h2>
    <p>You are logged in.</p>
  </div>
);

function App() {
  const [user, setUser] = React.useState(null);
  const isAuthenticated = !!localStorage.getItem('token');

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={isAuthenticated ? <Navigate to="/dashboard" /> : <HomePage />}
        />
        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route path="/register" element={<Register setUser={setUser} />} />
        <Route
          path="/dashboard"
          element={
            isAuthenticated ? (
              <Dashboard user={user} />
            ) : (
              <Navigate to="/" />
            )
          }
        />
      </Routes>
    </Router>
  );
}

// Simple CSS-in-JS styles
const styles = {
  container: {
    minHeight: '100vh',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    background: '#f6f8fa',
  },
  buttonGroup: {
    display: 'flex',
    gap: '1rem',
    marginTop: '2rem',
  },
  button: {
    padding: '0.75rem 2rem',
    fontSize: '1rem',
    borderRadius: '5px',
    border: 'none',
    background: '#007bff',
    color: '#fff',
    cursor: 'pointer',
    transition: 'background 0.2s',
  },
};

export default App;
