import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const MainHomepage = () => {
  const [query, setQuery] = useState('');
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    setJobs([]);
    try {
      // Example with Jobicy API (remote IT jobs, no key needed)
      const res = await axios.get('https://jobicy.com/api/v2/remote-jobs');
      const allJobs = res.data.jobs;
      // Filter by query (skills, title, etc.)
      const filtered = allJobs.filter(job =>
        job.title.toLowerCase().includes(query.toLowerCase()) ||
        job.company.toLowerCase().includes(query.toLowerCase()) ||
        (job.tags && job.tags.join(' ').toLowerCase().includes(query.toLowerCase()))
      );
      setJobs(filtered);
    } catch (err) {
      setJobs([]);
    }
    setLoading(false);
  };

  return (
    <div style={{ maxWidth: 800, margin: "2rem auto", padding: "2rem", background: "#fff", borderRadius: 8 }}>
      <h2>Search IT Jobs</h2>
      <form onSubmit={handleSearch} style={{ display: 'flex', gap: '1rem', marginBottom: '2rem' }}>
        <input
          type="text"
          placeholder="Search by skill, company, or job title"
          value={query}
          onChange={e => setQuery(e.target.value)}
          style={{ flex: 1, padding: '0.5rem', fontSize: '1rem' }}
        />
        <button type="submit" style={{ padding: '0.5rem 2rem' }} disabled={loading}>
          {loading ? 'Searching...' : 'Search'}
        </button>
      </form>
      <div>
        {jobs.length > 0 && (
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {jobs.map(job => (
              <li key={job.id} style={{ marginBottom: '1.5rem', borderBottom: '1px solid #eee', paddingBottom: '1rem' }}>
                <strong>{job.title}</strong> at {job.company}<br />
                <span>{job.location}</span><br />
                <span>Tags: {job.tags && job.tags.join(', ')}</span><br />
                <a href={job.url} target="_blank" rel="noopener noreferrer">View Job</a>
              </li>
            ))}
          </ul>
        )}
        {jobs.length === 0 && !loading && <p>No jobs found. Try a different search.</p>}
      </div>
      <button
        style={{ marginTop: '2rem', padding: '0.75rem 2rem', fontSize: '1rem' }}
        onClick={() => navigate('/homepage')}
      >
        Go to Homepage
      </button>
    </div>
  );
};

export default MainHomepage;
